CREATE DATABASE IF NOT EXISTS `config_server_db`;

USE config_server_db;
CREATE TABLE PROPERTIES (
  ID INT NOT NULL AUTO_INCREMENT, 
  APPLICATION TEXT, 
  PROFILE TEXT, 
  LABEL TEXT, 
  PROP_KEY TEXT,
  VALUE TEXT,
  PRIMARY KEY (ID)
 );
 GRANT ALL ON *.* TO 'user'@'%';

 select * from PROPERTIES;
 
 
 
 
 

 

